/*定义一个全局变量*/
window.itcast={};/*存储我们将要封装的事件方式*/
/*设置属性 定义一个transitionEnd一个事件绑定方法*/
itcast.transitionEnd=function(dom,callback){
	/*过滤结束事件的绑定
	1、谁需要绑定transitionEnd
	2、需要处理的函数*/
	if(dom && typeof dom === 'object'){
		dom.addEventListener('webkitTransitionEnd',function(){
			/*if (callback){
				callback();
			}*/
			callback && callback();
		});
		dom.addEventListener('transitionEnd',function(){
			callback && callback();
		});
	}
}

/*搜索区块的颜色变化*/
function head() {
	/*1、颜色随着页面的滚动逐渐加深
	2、当我们超过轮播图的时候，颜色保持不变*/

	/*获取搜索盒子*/
	var searchBox = document.querySelector('.header_box');
	var appnameBox = document.querySelector('.appname');
	/*获取banner盒子*/
	var bannerBox = document.querySelector('.banner');
	/*获取高度*/
	var h = bannerBox.offsetHeight;

	/*监听window的滚动事件*/

	window.onscroll = function() {
		/*不断的获取离顶部的距离*/
		var top = document.body.scrollTop;
		var opacity = 0;
		var co = "#ffffff";
		if(top < h) {
			/*1.颜色随着 页面的滚动  逐渐加深*/
			opacity = top / h * 0.85;
		} else {
			/*2.当我们超过轮播图的时候  颜色保持不变*/
			opacity = 0.85;
		}

		/*把透明度设置上去*/
		searchBox.style.background = "rgba(0,190,175," + opacity + ")";
		appnameBox.style.color = "rgba(51,51,51," + opacity + ")";
	}
}

/*轮播图*/
function banner() {
	/*1、自动的滚动起来 （定时器，过渡）
	2、点随之滚动起来  （改变当前点元素的样式）
	3、图片滑动        （touch事件）
	4、当不超过一定的滑动距离的时候，吸附回去 定位回去
	5、当超过了一定的距离的时候 ，滚动到上一张或下一张*/

	/*获取到dom对象*/
	/*banner*/
	var banner = document.querySelector('.banner');
	/*屏幕的宽度*/
	var w = banner.offsetWidth;
	/*图片盒子*/
	var imageBox = banner.querySelector('ul:first-child');
	/*点盒子*/
	var pointBox = banner.querySelector('ul:last-child');
	/*所有的点*/
	var points = pointBox.querySelectorAll('li');

	/*添加过渡*/
	var addTransition = function() {
			imageBox.style.webkitTransition = "all .2s"; /*兼容*/
			imageBox.style.transition = "all .2s";
		}
		/*删除过渡*/
	var removeTransition = function() {
			imageBox.style.webkitTransition = "none"; /*兼容*/
			imageBox.style.transition = "none";
		}
		/*改变位置*/
	var setTranslateX = function(translateX) {
		imageBox.style.webkitTransform = "translateX(" + translateX + "px)"; /*兼容*/
		imageBox.style.transform = "translateX(" + translateX + "px)";
	}

	/*1、自动的滚动起来 （定时器，过渡）*/
	var index = 1;
	var timer = setInterval(function() {
		/*箱子滚动*/
		index++;
		/*定位 过渡来做定位的，这样才有动画加过渡*/
		addTransition();
		/*改变位置*/
		setTranslateX(-index * w);
	}, 4000);

	/*绑定一个过渡结束事件*/
	itcast.transitionEnd(imageBox, function() {
		//console.log('transitionEnd');
		if(index >= 4) {
			index = 1;
			/*做定位
			加过渡*/
			removeTransition();
			setTranslateX(-index * w);
		} else if(index <= 0) {
			index = 3;
			/*加过渡*/
			removeTransition();
			/*改变位置*/
			setTranslateX(-index * w);
		}
		/*index 1-3 索引范围*/
		/*point 0-2*/
		setPoint();
	});

	/*2、点随之滚动起来  （改变当前点元素的样式）*/
	var setPoint = function() {
		/*把所有点的样式清除*/
		for(var i = 0; i < points.length; i++) {
			points[i].className = " ";
		}
		points[index - 1].className = "now";
	}

	/*3.图片滑动 touch事件）*/
	var startX = 0;
	var moveX = 0;
	var distanceX = 0;
	var isMove = false;

	imageBox.addEventListener('touchstart', function(e) {
		/*清除定时器*/
		clearInterval(timer);
		startX = e.touches[0].clientX;
	});
	imageBox.addEventListener('touchmove', function(e) {
		isMove = true;
		moveX = e.touches[0].clientX;
		distanceX = moveX - startX; /*distanceX  值  正负*/
		/*算出当前图片盒子需要定位的位置*/
		//console.log(distanceX);
		/*将要去做定位*/
		var currX = -index * w + distanceX;
		/*删除过渡*/
		removeTransition();
		/*改变位子*/
		setTranslateX(currX);
	});
	imageBox.addEventListener('touchend', function(e) {
		/*当超过了一定的距离的时候*/
		if(isMove && (Math.abs(distanceX) > w / 3)) {
			/*5.当超过了一定的距离的时候  滚动到上一张或下一张 （一定的距离  1/3  屏幕宽度 过渡）*/
			if(distanceX > 0) {
				index--; /*向右滑，上一张*/
			} else {
				index++; /*向左滑，下一张*/
			}
			addTransition();
			setTranslateX(-index * w);
		}
		/*当不超过一定的滑动距离的时候*/
		else {
			/*4.当不超过一定的滑动距离的时候  吸附回去  定位回去     （一定的距离  1/3  屏幕宽度  过渡）*/
			addTransition();
			setTranslateX(-index * w);
		}

		/*重置*/
		startX = 0;
		moveX = 0;
		distanceX = 0;
		isMove = false;

		/*添加定时器*/
		clearInterval(timer);
		timer = setInterval(function() {
			/*箱子滚动*/
			index++;
			/*定位  过渡来做定位的  这样才有动画*/
			/*加过渡*/
			addTransition();
			/*改变位子*/
			setTranslateX(-index * w);
		}, 4000);
	});

}

/*选项卡切换*/
function footer() {
	var oAs = $('#footer').find('a');
	for(var i = 0; i < oAs.length; i++) {
		$($(oAs)[i]).click(function() {
			for(var j = 0; j < oAs.length; j++) {
				var newsrc1 = '',
					newsrc2 = '';
				var src = $($(oAs)[j]).find('img').attr('src');
				var index = src.lastIndexOf('.');
				var src1 = src.substring(0, index);
				var src2 = src.substring(index, src.length);
				var src3 = src.substring(0, index - 3);
				var src4 = src.substring(index - 3, index);
				if(src4 == '_on') {
					newsrc1 = src3 + '' + src2;
					$($(oAs)[j]).find('img').attr('src', newsrc1);
				} else {
					newsrc2 = src1 + '' + src2;
					$($(oAs)[j]).find('img').attr('src', newsrc2);
				}
				var now_p = $($(oAs)[j]).find('p');
				$(now_p).removeClass('tab_p_on');

				$($('.layout').find('.tabbox')).css('display', 'none');
			}

			var idid = $(this).attr('id');
			if(idid == 'index') {
				var tabbox1 = $('.layout').find('.tabbox')[0];
				$(tabbox1).css('display', 'block');
			} else if(idid == 'order') {
				var tabbox2 = $('.layout').find('.tabbox')[1];
				$(tabbox2).css('display', 'block');
			} else if(idid == 'member') {
				var tabbox3 = $('.layout').find('.tabbox')[2];
				$(tabbox3).css('display', 'block');
			} else if(idid == 'about') {
				var tabbox4 = $('.layout').find('.tabbox')[3];
				$(tabbox4).css('display', 'block');
			}

			var src_1 = $(this).find('img').attr('src');
			var index = src_1.lastIndexOf('.');
			var src1_1 = src_1.substring(0, index);
			var src2_1 = src_1.substring(index, src_1.length);
			var newsrc = src1_1 + '_on' + src2_1;
			$(this).find('img').attr('src', newsrc);
			$(this).find('p').addClass('tab_p_on');
		});
	}
}

